class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        if n == 1:
            return 0
        res = 0
        cnt = 0
        i = 0
        while i < n:
            if s[i] == '1':
                cnt += 1
                i += 1
            else:
                j = i+1
                while j < n and s[j] == '0':
                    j += 1
                res += cnt 
                i = j
        return res
