class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int x=0,cur=0;
        for(int i=n-1;~i;--i){
            if(s[i]=='0'){
                if(i==n-1 or s[i+1]=='1'){++cur;}
            }
            else{x+=cur;}
        }
        return x;
    }
};
