class Solution {
public:
    int maxOperations(string s) {
        int n=s.size(),cnt=0;
        bool f=0;
        vector<int> v;
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                f=1;
                cnt++;
            }
            if((s[i]=='0') && f){
                v.push_back(cnt);
                f=0;
                cnt=0;
            }
        }
        // for(auto i:v) cout<<i<<" ";
        // cout<<endl;

        int sz=v.size();
        for(int i=1;i<sz;++i){
            v[i] +=v[i-1];
        }
        int sum=0;
        for(int i=0;i<sz;++i) sum+=v[i];
        return sum;
    }
};
