class Solution:
    def maxOperations(self, s: str) -> int:
        cnt = []
        now = 0
        pre = -1
        for c in s[::-1]:
            if c == '1':
                if pre == -1:
                    continue
                elif pre == 0:
                    now += 1
                    pre = 1
                else:
                    now += 1
            else:
                if pre == 1:
                    cnt.append(now)
                    now = 0
                    pre = 0
                elif pre == -1:
                    pre = 0
        cnt.append(now)
        ans = 0
        for idx, c in enumerate(cnt):
            ans += (idx + 1) * c
        return ans
