class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        curr = 0
        i = 0
        while i < len(s)-1:
            if s[i] == '1':
                curr += 1
                if s[i+1] == '0':
                    ans += curr
                    #print(s[i+1:].find('1'))
                    if s[i+1:].find('1') == -1:
                        return ans
                    else:
                        i = s[i+1:].find('1') + i
            i += 1  

        return ans
