class Solution:
    def maxOperations(self, s: str) -> int:
        cnts = []
        cnt = 0
        for i, c in enumerate(s):
            if c == "1":
                cnt += 1
                if i > 0 and s[i-1] == "1":
                    cnts[-1] = cnt
                else:
                    cnts.append(cnt)
        # print(cnts)
        if s[-1] == "1":
            cnts.pop()
        return sum(cnts)
