class Solution {
public:
    int maxOperations(string s) {
        int result=0,on=0;
        bool check=true;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                on++;
                check=true;
            }
            else{
                if(check)result+=on;
                check=false;
            }
        }
        return result;
    }
};
