class Solution {
public:
    int maxOperations(string s) {
        int ans=0,cnt1=0;
        int flag=0;
        int n=s.size();
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                cnt1++;
                flag=1;
            }
            if(s[i]=='0'&&flag){
                ans+=cnt1;
                flag=0;
            }
        }
        return ans;        
    }
};
