class Solution:
    def maxOperations(self, s: str) -> int:
        maxOps = 0
        currentOnes = 0

        idx = 0
        while idx < len(s):
            if s[idx] == '1':
                currentOnes += 1
                idx += 1
            else:
                while idx < len(s) and s[idx] == '0':
                    idx += 1

                maxOps += currentOnes


        return maxOps
