class Solution {
public:
    int maxOperations(string s) {
        int res = 0,cur = 0;
        int delta = 0;
        for(int i = s.size()-1;i>=0;--i){
            if(s[i] == '1'){
                cur += delta;
                res += cur;
                delta = 0;
            }else if(s[i] == '0'){
                delta = 1;
            }
        }
        return res;
    }
};
