class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int r = 0;
        int l = 0;
        int cnt = 0;
        int result = 0;
        while(r < n){
            while(r < n && s[r] == '0') r++;
            while(r < n && s[r] == '1'){
                r++;
                cnt++;
            }
            if(r == n) break;
            result += cnt;
        }
        return result;
    }
};
