class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int cnt = 1;
        int n = s.size();
        for(int i = n - 1; i >= 0; --i) {
            if(i - 1 >= 0 && s[i] == '0' && s[i - 1] == '1') {
                int sum = 0;
                while(i - 1 >= 0 && s[i - 1] == '1') {
                    sum++;
                    --i;
                }
                res += cnt * sum;
                cnt++;
            }
        }
        return res;
    }
};
