class Solution {
public:
    int maxOperations(string s) 
    {
        queue<int> q;
        int num=0;
        for (int i=0;i<s.size();++i)
        {
            if (s[i]=='1')
            {
                while (i+num<s.size() && s[i+num]=='1')
                    ++num;
                q.push(num);
                i+=num-1;
                num=0;
            }
        }
        int res=0;
        int now=0;
        while (!q.empty())
        {
            now+=q.front();
            q.pop();
            res+=now;            
        }
        if (s[s.size()-1]!='1')
            return res;
        return res-now;
    }
};
