impl Solution {
    pub fn max_operations(s: String) -> i32 {
        let s = s.as_bytes();
        let mut movable = 0;
        let mut left_ones = 0;
        for i in 0..s.len() {
            if s[i] == b'0' && (i == s.len() - 1 || s[i + 1] != b'0') {
                movable += left_ones;
            } else if s[i] == b'1' {
                left_ones += 1;
            }
        }
        movable
    }
}
