class Solution {
public:
    int maxOperations(string s) {
        int ans=0,z=0;
        bool c=true;
        int n=s.length();
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                z++;
                c=true;
            }
            else{
                if(c) ans+=z;
                c=false;
            }
        }
        return ans;
    }
};
