class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int ind=-1;
        for(int i=n-1;i>=0;i--){
            if(s[i]=='0'){
                ind=i;
                break;
            }
        }
        if(ind==-1)return 0;
        int ans=0;
        int cnt=0;
        for(int i=0;i<max(ind,n-1);i++){
             if(s[i]=='0' && s[i+1]=='1'){
                ans+=cnt;
             }
             cnt+=(s[i]=='1');
        }
        if(s[n-1]=='0'){
            ans+=cnt;
        }
        return ans;
    }
};
