class Solution {
    public int maxOperations(String s) {
        int ret = 0;
        int n = s.length();
        int i = 0;
        int x = 0;
        while (i < n) {
            while (i < n && s.charAt(i) == '1') {
                i ++;
                x ++;
            }
            if (i < n) {
                while (i < n && s.charAt(i) == '0') {
                    i ++;
                }
                ret += x;
            }
        }
        return ret;
    }
}
