class Solution {
public:
    int maxOperations(string s) {
        int zeri=0;
        int flag=0;
        int ans=0;
        for(int i=s.length()-1;i>=0;i--)
        {
            if(s[i]=='0')
            {
                if(flag==0)
                {
                    zeri++;
                    flag=1;
                }
                else
                    continue;
            }
            else
            {
                ans+=zeri;
                flag=0;
            }
        }
        return ans;
    }
};
