class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int rec=0;
        int cnt=0;
        int n=s.length();
        for(int i=n-1; i>=0; i--)
        {
            if(s[i]=='0')
            {
                cnt++;
            }
            else{
                int temp=rec;
                if(cnt>0)
                {
                    temp++;
                }
                ans+=temp;
                rec=temp;
                cnt=0;
            }
        }
        return ans;
    }
};
