class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        for (int i = 0, one_count = 0; i < s.size(); ++i) {
            if (s[i] == '0') {
                res += one_count;
                while (i < s.size() && s[i] == '0') {
                    ++i;
                }
                --i;
            }
            else {
                ++one_count;
            }
        }
        return res;
    }
};
