class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        int ans = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s[i] == '1') {
                cnt++;
                // printf("i == %d cnt==%d\n", i, cnt);
            }
            else if (i > 0 && s[i-1] == '1'){
                ans += cnt;
                // printf("i==%d ans ==%d\n", i, ans);
            }
        }
        return ans;
    }
};
