class Solution {
    public int maxOperations(String s) {
        int result = 0;

        int ones = 0;
        int gap = 0;
        int n = s.length();

        for(int i = 0; i < n; i++){
            char c = s.charAt(i);
            if(c == '1'){
                if(ones != 0 && gap != 0){
                    result += ones;
                }
                ones++;
                gap = 0;
            }
            else gap++;
        }
        if(ones != 0 && gap != 0){
            result += ones;
        }
        return result;
    }
}
