class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        prevOne = 0

        for i in range(len(s)):
            c = s[i]
            if c == '1':
                prevOne += 1
            elif c == '0':
                if i != 0 and s[i-1] != '0':
                    ans += prevOne
        return ans
