using namespace std;
#include<bits/stdc++.h>
#define ll long long
#define pb push_back
#define all(x) x.begin(), x.end()
#define float long double
#define double long double
#define sortall(x) sort(all(x))
#define sz(a) (int)(a).size()
const int mod = 1e9+7;
//<--------------------------------------------------------------------------------->

class Solution {
public:
    int maxOperations(string s) {
        int count = 0;
        int ans = 0;
        int zero = 0;
        for(int i=0;i<sz(s);i++){
            char it = s[i];
            if(it=='1'){
                if(zero==1){
                    ans+=count;
                }
                count++;
                zero = 0;
            }
            else if(i==(sz(s)-1)){
                zero = 1;
                ans+=count;
            }
            else zero=1;
        }
        return ans;
    }
};
