class Solution {
public:
    int maxOperations(string s) {
        int c1 = 0;
        int cnt = 0;
        int i=0;
        int n = s.length();
        while(i<n)
        {
            if(s[i] == '1')
                c1++;
            else
            {
                while(i<n-1 && s[i+1]=='0')
                    i++;
                cnt += c1;
            }
            i++;
        }
        return cnt;
    }
};
