class Solution:
    def maxOperations(self, s: str) -> int:
        a = []
        for gr in groupby(s):
            if gr[0] == '1':
                a.append(sum(1 for _ in gr[1]))

        ans = 0
        total = 0
        for x in a:
            ans += total
            total += x

        if s[-1] == '0':
            ans += total
        return ans
