class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
    int p = 0;
    bool has_one = false;
    vector<int> arr;

    for (char c : s) {
        if (c == '1') {
            arr.push_back(1);
            has_one = true;
        } else if (c == '0' && has_one) {
            arr.push_back(0);
            has_one = false;
        }
    }

    for (int pr : arr) {
        if (pr == 1) {
            p += 1;
        } else {
            ans += p;
        }
    }

    return ans;
    }
};
