class Solution {
    fun maxOperations(s: String): Int {
        var res = 0
        var ones = 0
        var curr = 0
        for(c in s){
            if(c == '1'){
                res += curr
                curr = 0
                ones++
            }else {
                curr = ones
            }
        }
        res += curr
        return res
    }
}
