class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, c = 0;
        bool zero = false;
        for (int i=s.size()-1;i>=0;i--) {
            if (s[i]=='0') zero = true;
            else {
                if (zero) c++;
                zero = false;
                ans += c;
            }
        }
        return ans;
    }
};
