class Solution {
    public int maxOperations(String s) {
        int cnt=0,ans=0;
        boolean done = true;
        for(int i=0;i<s.length();i++)
        {
            if(s.charAt(i)=='1')
            {
                cnt++;
                done = false;
            }
            else
            {
                if(done==false)
                {
                    ans+=cnt;
                    done =true;
                }
                else
                {
                    continue;
                }
            }
        }
        return ans;
    }
}
