class Solution {
public:
    int maxOperations(string s) {
        int count1=0;
        int ans=0;
        for(int i=0;i<s.size();i++)
        {
            if(s[i]=='1')
            {
                count1++;
            }
            else{
                ans+=count1;
                while(i+1<s.size() && s[i+1]!='1')
                {
                    i++;
                }
            }
        }
        return ans;
    }
};
