import java.util.*;

class Solution {
    public int maxOperations(String binaryString) {
        int operationsCount = 0, onesCounter = 0;
        List<Integer> sequenceList = new ArrayList<>();
        boolean encounteredOne = false;


        // Traverse the binary string
        for (int index = 0; index < binaryString.length(); index++) {
            if (binaryString.charAt(index) == '1') {
                sequenceList.add(1);
                encounteredOne = true;
            }
            if (binaryString.charAt(index) == '0' && encounteredOne) {
                sequenceList.add(0);
                encounteredOne = false;
            }
        }

        // Compute the total number of operations
        for (int element : sequenceList) {
            if (element == 1) {
                onesCounter++;
            } else {
                operationsCount += onesCounter;
            }
        }

        return operationsCount;
    }
}
