class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), cnt = 0, ans = 0;
        for(int i=0;i<n-1;i++){
            if(s[i] == '0' && s[i+1] == '1'){
                ans += cnt;
            }else if(s[i] == '1'){
                cnt++;
            }
        }
        if(s[n-1] == '0') ans += cnt;
        return ans;
    }
};
