class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, cnt = 0, f = 1;
        for(int i = s.size()-1 ; i >= 0 ; i--) {
            if(s[i] == '0') {
                if(f == 1) cnt++;
                f = 0;
            } else {
                f = 1;
                ans += cnt; 
            }
        }
        return ans;
    }
};
