class Solution:
    def maxOperations(self, s: str) -> int:
        # greedy, we want to always pick the leftmost ones first
        # at each point where we have '10', all the other '1's before it can shift down too
        count = 0

        ret = 0
        for i in range(len(s)):
            if s[i] == "1":
                count += 1
            if i + 1 < len(s) and s[i] == "1" and s[i + 1] == "0":
                ret += count
                # print((count, i))
        return ret
