class Solution {
    public int maxOperations(String s) {
        int last = 0;
        int cur = 0;
        int ans = 0;
        for(int i = 0; i < s.length(); i++) {
            if(s.charAt(i) == '1') {
                cur++;
            } else {

                if(cur != 0) {
                    ans += last;
                    last += cur;
                    cur = 0;
                }

            }
        }

        ans += last;
        return ans;
    }
}
