def GP(it): return [[ch,len(list(g))] for ch,g in groupby(it)]
class Solution:
    def maxOperations(self, s: str) -> int:
        g=GP(s)
        ans=0
        l=len(g)
        if g[-1][0]=='1':
            cnt=1
            for i in range(l-3,-1,-2):
                ans+=g[i][1]*cnt
                cnt+=1
        else:
            cnt=1
            for i in range(l-2,-1,-2):
                ans+=g[i][1]*cnt
                cnt+=1
        return ans
