import Foundation

class Solution {
    func maxOperations(_ s: String) -> Int {
        var operations = 0
        var oneCount = 0
        var encounteredOne = false
        var bitSequence = [Int]()

        for ch in s {
            if ch == "1" {
                bitSequence.append(1)
                encounteredOne = true
            } else if ch == "0" && encounteredOne {
                bitSequence.append(0)
                encounteredOne = false
            }
        }

        for bit in bitSequence {
            if bit == 1 {
                oneCount += 1
            } else {
                operations += oneCount
            }
        }

        return operations
    }
}

// Function to call the main logic
func testMaxOperations() {
    let solution = Solution()
    let inputString = "110100110" // Example input
    let result = solution.maxOperations(inputString)
    print("Maximum operations: \(result)")
}

// Call the test function
testMaxOperations()
