class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans = 0;
        int one = 0;
        bool flag = false;

        for (char c : s) {
            if (c == '1') {
                one++;
                flag = true;
            } else if (c == '0' && flag) {
                ans += one;
                flag = false;
            }
        }

        return ans;
    }
};
