class Solution {
public:
using ll=long long;
    int maxOperations(string s) {
       ll cnt=0;
       ll oc=0;
       char prev='x';
       for(ll i=0;i<s.size();i++) 
       {
          if(s[i]=='0')
          {
            if(prev=='0')
          {
            continue;
          }
            cnt+=oc;
          }else
          {
            oc++;
          }
          prev=s[i];
       }
       return cnt;
    }
};
