class Solution {
public:
    int go(int ind , int t, string& s, vector<vector<int>>& dp) {
        if(ind == s.length())   return 0;

        if(dp[ind][t] != -1)    return dp[ind][t];
        int ans = go(ind+1, t, s, dp);
        if((s[ind] - '0') == t)
            ans = max(ans, 1 + go(ind+1, 1 - t, s, dp));
        return dp[ind][t] = ans;
    }
    int maxOperations(string s) {
        int n = s.length();
        vector<vector<int>> dp(n, vector<int> (2, -1));
        int ans = 0;
        for(int i = 0; i < s.length(); i ++) {
            if(s[i] == '1') {
                int len = go(i+1, 0, s, dp);
                //cout << i << " "<< len << endl;
                if(len != -1)
                    ans += (len + 1) / 2;
            }
        }
        return ans;
    }
};
