class Solution { 
public: 
    int maxOperations(string s) { 
        int ans = 0, p = 0; 
        bool k = false; 

        for(char c : s) { 
            if(c == '1') { 
                p++; 
                k = true; 
            } 
            if(c == '0' && k) { 
                ans += p; 
                k = false; 
            } 
        } 

        return ans; 
    } 
};
