class Solution {
    public int maxOperations(String s) {
         int ans = 0;
        int countOnes = 0;
        boolean hasOne = false;

        // Traverse the string and calculate the number of operations
        for (char c : s.toCharArray()) {
            if (c == '1') {
                countOnes++;  // Count number of 1s
                hasOne = true;
            } else if (c == '0' && hasOne) {
                ans += countOnes;  // Add count of 1s before this 0
                hasOne = false;    // Reset flag for 1s
            }
        }

        return ans;
    }
}
