class Solution:
    def maxOperations(self, s: str) -> int:
        grp = groupby(s)
        arr = []
        for a, b in grp:
            if a == '1':
                arr.append(len(list(b)))
            else:
                arr.append(0)
        # print(arr)
        cum = ans = 0
        for i in range(len(arr)):
            if arr[i]:
                ans += cum
                cum += arr[i]
        if arr[-1] == 0:
            ans += cum
        return ans
