class Solution {
    public int maxOperations(String s) {
        int ans=0;
        int count=0;

        int n=s.length();

        int idx=0;
        while(idx<n){

            char ch=s.charAt(idx);
            if(ch=='1'){
                count++;
                idx++;
            }
            else{

                while(idx<n && s.charAt(idx)=='0'){
                    idx++;
                }

                ans+= count;
            }

        }

        return ans;
    }
}
