class Solution {
public:
    int maxOperations(string s) {
        int operations = 0;
        int ones = 0, zeros = 0;
        for (char c : s) {
            if (c == '1') {
                if(zeros > 0){
                    operations += ones;
                    zeros = 0;
                }
                ones++;
            } else if (c == '0') {
                zeros=1;
            }
        }
        if(zeros > 0){
            operations += ones;
            zeros = 0;
        }
        return operations;
    }
};
