class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int cnt=0;
        int n = s.size();
        int i=n-1;
        while(i>=0){
            if(s[i]=='1'){
                ans += cnt;
                i--;
            }else{
                while(i>=0 && s[i]=='0'){
                    i--;
                }
                cnt++;
            }
        }
        return ans;

    }
};
