class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        vector<int> dp(n + 1,0);
        int clump = 0;
        int zero = 0;
        int res = 0;
        for(int i = 0;i < n;i++){
            if(s[i] == '1'){
                while(i < n && s[i] == '1'){
                    clump++;
                    i++;
                }
                if(i >= n){
                    break;
                }
                if(s[i] == '0'){
                    res += clump;
                }
            }
        }
        return res;
    }
};
