class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int countOnes = 0;
        boolean hasOne = false;
        for (char c : s.toCharArray()) {
            if (c == '1') {
                countOnes++;
                hasOne = true;
            } else if (c == '0' && hasOne) {
                ans += countOnes;
                hasOne = false;
            }
        }
        return ans;
    }
}
