class Solution:
    def maxOperations(self, s: str) -> int:

        """
        boldly assume:
            min ops = always move rightmost movable first
            max ops = always move leftmost movable first
        """
        n=len(s)
        cnt=s.count("1")
        #print(n,cnt)
        cur=0
        ans=0
        for i in range(n):
            if s[i]=="1" :
                cur+=1
                if i+1<n and s[i+1]=="0":
                    ans+=cur
            else:
                pass


        return ans
